function cpfMask(numbercpf) {
  var cpf = numbercpf.value;

  //formato = 111.111.111-11
  
  if (isNaN(cpf[cpf.length - 1])) {
    // Proibir caractere que não seja número
    numbercpf.value = cpf.substring(0, cpf.length - 1);
    return;
  }

  if (cpf.length === 3 || cpf.length === 7) {
    numbercpf.value += ".";
  }
  if (cpf.length === 11) {
    numbercpf.value += "-";
  }
}

function maskPhone(numberPhone) {
  var phone = numberPhone.value;

  //formato = (11)91111-1111

  if (phone.length < 14) {
    phone = phone.replace(/\D/g, "");

    phone = phone.replace(/^(\d{2})(\d)/g, "($1)$2");
    phone = phone.replace(/(\d)(\d{3})$/, "$1-$2");
    numberPhone.value = phone;
  }
}

function maskdateDDMMAAAA(dateMask) {
  var dateFormat = dateMask.value;

  //formato =   00/00/0000

  if (isNaN(dateFormat[dateFormat.length - 1])) {
    // Proibir caractere que não seja número
    dateMask.value = dateFormat.substring(0, dateFormat.length - 1);
    return;
  }

  if (dateFormat.length > 1 && dateFormat.length < 3) {
    dateMask.value += "/";
  }

  if (dateFormat.length > 4 && dateFormat.length < 6) {
    dateMask.value += "/";
  }
}


function maskdateDDMMAAAAHHMM(dateMask) {
  var dateFormat = dateMask.value;

  //formato =   00/00/0000 00:00

  if (isNaN(dateFormat[dateFormat.length - 1])) {
    // Proibir caractere que não seja número
    dateMask.value = dateFormat.substring(0, dateFormat.length - 1);
    return;
  }

  if (dateFormat.length > 1 && dateFormat.length < 3) {
    dateMask.value += "/";
  }

  if (dateFormat.length > 4 && dateFormat.length < 6) {
    dateMask.value += "/";
  }

  if (dateFormat.length > 9 && dateFormat.length < 11) {
    dateMask.value += " ";
  }

  if (dateFormat.length > 12 && dateFormat.length < 14) {
    dateMask.value += ":";
  }
}

